import * as crypto from "crypto";
const ed25519 = require('ed25519');

// Result of GRPC call to rpctransact's FormulateTx.
export interface BurrowUnsignedTransaction {
  ChainID: string;
  Type: string;
  Payload: {
    Input: {
      Address: string;
      Sequence?: number;
    }
    Address: string;
    GasLimit: number;
    Data: string;
  }
}

// Param for GRPC call to rpctransact's BroadcastTxSync.
export interface BurrowSignedTransaction {
  Envelope: {
    Signatories: Array<{
      Address: Buffer;
      PublicKey: {
        CurveType: number;
        PublicKey: Buffer;
      }
      // The data in 'Tx', signed with the private key of the sender
      Signature: Buffer;
    }>;
    // This will be the JSON.stringified data of BurrowUnsignedTransaction,
    // with Payload.Input.Sequence filled in.
    Tx: Buffer;
  }
}

export class BurrowKeyPair {
  readonly address: Buffer;
  readonly publicKey: Buffer;
  readonly privateKey: Buffer;

  constructor(address: Buffer, publicKey: Buffer, privateKey: Buffer) {
    this.address = address;
    this.publicKey = publicKey;
    this.privateKey = privateKey;
  }

  sign(tx: BurrowUnsignedTransaction, nonce: number) : BurrowSignedTransaction {
    tx.Payload.Input.Sequence = nonce+1; // Burrow nonces start counting at 1, as opposed to 0 with Ethereum

    const message = Buffer.from(JSON.stringify(tx));
    const signature = ed25519.Sign(message, this);
    const signatureWithPrefix = Buffer.concat(
      [Buffer.from("08011240", 'hex'), // Burrow expects these 4 bytes to precede the signature. They probably indicate the CurveType, although it's a strange thing.
      signature]);

    const signedTx = {
      Envelope: {
        Signatories: [
          {
            Address: this.address,
            PublicKey: {
              CurveType: 1,
              PublicKey: this.publicKey,
            },
            Signature: signatureWithPrefix,
          },
        ],
        Tx: message,
      },
    };

    return signedTx;
  }

  static generate() : BurrowKeyPair {
    const randomSeed = crypto.randomBytes(32);
    const {publicKey, privateKey} = ed25519.MakeKeypair(randomSeed);
    const address = crypto.createHash('sha256').update(publicKey).digest().slice(0,20);
    return new BurrowKeyPair(address, publicKey, privateKey);
  }
}

