// query callback representing the query of a chaincode
func (t *SimpleChaincode) query(stub shim.ChaincodeStubInterface, args []string) pb.Response {
	var Username string // Entities
	var err error

	if len(args) != 1 {
		return shim.Error("Incorrect number of arguments. Expecting name of the person to query")
	}

	Username = args[0]

	// Get the state from the ledger
	Usernamevalbytes, err := stub.GetState(Username)
	if err != nil {
		jsonResp := "{\"Error\":\"Failed to get state for " + Username + "\"}"
		return shim.Error(jsonResp)
	}

	if Usernamevalbytes == nil {
		jsonResp := "{\"Error\":\"Nil value for " + Username + "\"}"
		return shim.Error(jsonResp)
	}

	jsonResp := "{\"Name\":\"" + Username + "\",\"Amount\":\"" + string(Usernamevalbytes) + "\"}"
	fmt.Printf("Query Response:%s\n", jsonResp)
	return shim.Success(Usernamevalbytes)
}

