func (c *ChannelController) QueryCC() {
	//获取input参数
	c.getTransctionInput()
	fmt.Println(input)
	//获取网络配置信息
	configfile := beego.AppConfig.String("FabricNetWorkConfigFile")
	var testa = 1
	fmt.Println("局部变量地址测试开始")
	fmt.Printf("%v", &testa)
	configprovider := config.FromFile(configfile)
	//添加替换信息配置文件
	configprovider = utils.AddLocalEntityMapping(configprovider)
	sdk, err := fabsdk.New(configprovider)
	if err != nil {
		c.responsefunc(false, "fabsdk.New() error", err.Error())
		return
	}
	defer sdk.Close()
	//prepare channel client context using client context
	clientChannelContext := sdk.ChannelContext(input.ChannelID, fabsdk.WithUser(input.UserName), fabsdk.WithOrg(input.OrgName))
	// Channel client is used to query and execute transactions (Org1 is default org)
	client, err := channel.New(clientChannelContext)
	if err != nil {
		c.responsefunc(false, "channel.New() error", err.Error())
		return
	}
	//将输入参数转化成[][]byte
	var args = make([][]byte, len(input.Args))
	for index, val := range input.Args {
		args[index] = []byte(val)
	}
	response, err := client.Query(channel.Request{ChaincodeID: input.ChaincodeID, Fcn: input.FuncID, Args: args},
		channel.WithRetry(retry.DefaultChannelOpts),
		channel.WithTargetEndpoints(input.Targets...),
	)
	if err != nil {
		c.responsefunc(false, "client.Query() error", err.Error())
	}
	c.responsefunc(true, "query successfully", string(response.Payload))
}

func (c *ChannelController) InvokeCC() {
	//获取input参数
	c.getTransctionInput()
	//获取网络配置信息
	configfile := beego.AppConfig.String("FabricNetWorkConfigFile")
	configprovider := config.FromFile(configfile)
	//添加替换信息配置文件
	configprovider = utils.AddLocalEntityMapping(configprovider)
	sdk, err := fabsdk.New(configprovider)
	if err != nil {
		c.responsefunc(false, "fabsdk.New() error", err.Error())
		return
	}
	defer sdk.Close()
	//prepare channel client context using client context
	clientChannelContext := sdk.ChannelContext(input.ChannelID, fabsdk.WithUser(input.UserName), fabsdk.WithOrg(input.OrgName))
	// Channel client is used to query and execute transactions (Org1 is default org)
	client, err := channel.New(clientChannelContext)
	if err != nil {
		c.responsefunc(false, "channel.New() error", err.Error())
		return
	}
	eventID := "testEvent"
	// Register chaincode event (pass in channel which receives event details when the event is complete)
	reg, notifier, err := client.RegisterChaincodeEvent(input.ChaincodeID, eventID)
	if err != nil {
		c.responsefunc(false, "Failed to register cc event", err.Error())
	}
	defer client.UnregisterChaincodeEvent(reg)
	var args = make([][]byte, len(input.Args))
	for index, val := range input.Args {
		args[index] = []byte(val)
	}
	_, err = client.Execute(channel.Request{ChaincodeID: input.ChaincodeID, Fcn: input.FuncID, Args: args},
		channel.WithRetry(retry.DefaultChannelOpts))
	if err != nil {
		c.responsefunc(false, "Failed to invoke chaincode", err.Error())
	}
	if flag == "1" {
		time.Sleep(time.Second * 10)
	}
	var ccEvent *fab.CCEvent
	select {
