	// 	}
	// }

	// Create a Gateway connection for a specific client identity
	gateway, err := client.Connect(id, client.WithSign(sign), client.WithClientConnection(clientConnection))
	// gateway, err := gateway.Connect(gateway.WithConfig(config.FromFile("/config.yaml")), gateway.WithIdentity(wallet, "appUser"))

	defer gateway.Close()

	if err != nil {
		zap.Logger.Panicf("failed to connect to gateway peer: %s", err)
	}

	Gateway = gateway
}

// newGrpcConnection creates a gRPC connection to the Gateway server.
func newGrpcConnection() *grpc.ClientConn {
	certificate, err := loadCertificate(tlsCertPath)
	if err != nil {
		zap.Logger.Panicf("failed to obtain commit status: %w", err)
	}

	certPool := x509.NewCertPool()
	certPool.AddCert(certificate)
	transportCredentials := credentials.NewClientTLSFromCert(certPool, "")

	connection, err := grpc.Dial(peerEndpoint, grpc.WithTransportCredentials(transportCredentials))
	if err != nil {
		zap.Logger.Panicf("failed toevaluate transaction: %w", err)
	}

	return connection
}

// newIdentity creates a client identity for this Gateway connection using an X.509 certificate.
func newIdentity() *identity.X509Identity {
	certificate, err := loadCertificate(certPath)
	if err != nil {
		zap.Logger.Panic(err)
	}

	id, err := identity.NewX509Identity(mspID, certificate)
	if err != nil {
		zap.Logger.Panic(err)
	}

	return id
}

// newSign creates a function that generates a digital signature from a message digest using a private key.
func newSign() identity.Sign {
	privateKeyPEM, err := ioutil.ReadFile(keyPath)
	if err != nil {
		zap.Logger.Panic(err)
	}

	privateKey, err := identity.PrivateKeyFromPEM(privateKeyPEM)
	if err != nil {
		zap.Logger.Panic(err)
	}

	sign, err := identity.NewPrivateKeySign(privateKey)
	if err != nil {
		zap.Logger.Panic(err)
	}

	return sign
}

func loadCertificate(filename string) (*x509.Certificate, error) {
	certificatePEM, err := ioutil.ReadFile(filename)
	if err != nil {
		return nil, err
	}

	return identity.CertificateFromPEM(certificatePEM)
}

